import React, { useEffect, useState } from 'react'
import './details.css'
import heart from '../../images/heart.svg'

 function Details(props) {
const [product, setProduct]=useState()
useEffect(() => {
        setProduct(props?.productList)
}, [])
      
    return (
        <div>
{props.detailsOpen &&
<div className='wrapper2' id='wrapper2'
onClick={props.detailsClose}>
{product&&product?

product && product?.map((el, index)=>{
        console.log('data',el)
        return(
         <div className='modal1' id='modal1' key={index}>

<div className='detail-card' id='detail-card'>
        <div className='detail-card1' onClick={()=>props.infoOpen(el?.id)}>
        <img alt='img' className='detailshoot' src={el?.image}/>
                <img className='like'  id='like' src={heart}/>
                <h3>{el?.title}</h3>
                <h3>{el?.price}</h3>
                <button className='detail-btn'>BUY</button>
        </div> 
        </div>
</div>      
        )
}):<h5>empty</h5>
}

        </div>
}
        </div>
    )
}
export default Details
